package day01

fun main() {
    println(solvePart1("(())") == 0)
    println(solvePart1("()()") == 0)
    println(solvePart1("(((") == 3)
    println(solvePart1("(()(()(") == 3)
    println(solvePart1("))(((((") == 3)
    println(solvePart1("())") == -1)
    println(solvePart1("))(") == -1)
    println(solvePart1(")))") == -3)
    println(solvePart1(")())())") == -3)
    println(solvePart1(PUZZLE_INPUT) == 74)
    println(solvePart2(PUZZLE_INPUT) == 1795)
}

fun solvePart1(input: String): Int {
    return input.fold(0) { i, c -> if (c == '(') i + 1 else i - 1 }
}

fun solvePart2(input: String): Int {
    return input.runningFold(0) { i, c -> if (c == '(') i + 1 else i - 1 }
        .indexOf(-1)
}

