package day01

//class Puzzle {

/*
(()) and ()() both result in floor 0.
((( and (()(()( both result in floor 3.
))((((( also results in floor 3.
()) and ))( both result in floor -1 (the first basement level).
))) and )())()) both result in floor -3.
*/
fun main() {
    println(solve("(())") == 0)
    println(solve("()()") == 0)
    println(solve("(((") == 3)
    println(solve("(()(()(") == 3)
    println(solve("))(((((") == 3)
    println(solve("())") == -1)
    println(solve("))(") == -1)
    println(solve(")))") == -3)
    println(solve(")())())") == -3)
    println(solve(PART_1) == 74)
}

fun solve(input: String): Int {
    return input.fold(0) { i, c -> if (c == '(') i + 1 else i - 1 }
}

